import serial
import random
PARITY=serial.PARITY_NONE
STOPBITS=1
DATABITS=8
import time
from game import TronGame

# GLOBALS
GAME = TronGame(42, 3)
SER = serial.Serial(
    port='/dev/cu.usbserial-AM01AL8W',
    baudrate=1000000,
    parity=PARITY,
    stopbits=STOPBITS,
    bytesize=DATABITS)

# COMM FUNCTIONS
def wait_to_receive():
    board = []    
    while SER.inWaiting() == 0:
        time.sleep(1)
    while SER.inWaiting() > 0:
        b = SER.read(1).hex()
        board.append(b)
    return board 
def wait_to_transmit(action):
    dic = {0: "\x00", 1: "\x01", 2: "\x02", 3: "\x03"}
    SER.write(dic[action].encode("utf-8"))

# FPGA TO GAME
def get_attr(state):
    board = state[:1600]
    nu_board = []
    curr = 0
    for i in range(len(board)):
        if i % 40 == 0:
            nu_board.append(board[curr: curr + 40])
            curr += 40
    
    p_x = "".join([str(a) for a in state[1600: 1608]])
    p_y = "".join([str(a) for a in state[1608: 1616]])
    p_pos = [int(p_x, 2), int(p_y, 2)]

    e_x = "".join([str(a) for a in state[1616:1624]])
    e_y = "".join([str(a) for a in state[1624:1632]])
    e_pos = [int(e_x, 2), int(e_y, 2)]
    
    return nu_board, p_pos, e_pos
def get_action(board, p_pos, e_pos):
    GAME.board = board
    GAME.p_pos = p_pos
    GAME.e_pos = e_pos
    a = GAME.bot_turn()
    return a
